vec4 Effect2(vec2 uv) {
    // Create hexagonal pattern using polar coordinates
    float theta = atan(uv.y, uv.x);
    float r = length(uv * 1.5);

    // Add rotation over time
    theta += iTime * 0.2;

    // Create hexagonal grid
    vec2 pos = vec2(
        cos(theta) * r,
        sin(theta) * r
    );

    // Tile the plane with hexagons
    pos = mod(pos + 1.5, 3.0) - 1.5;

    // Calculate distance from center
    float d = length(pos);

    // Create gradient border effect
    d = abs(d - 0.4) * 3.0;

    // Add inner glow
    float glow = min(length(pos + vec2(0.2, 0.2)), 0.15);

    // Combine effects with noise
    vec3 color = hsl2rgb(vec3(
        (d + iTime) * 60.0,
        1.0 - d * 0.8,
        0.5 + turbulent(pos * 4.0 + iTime) * 0.5
    ));

    // Add inner glow to color
    color = mix(color, vec3(1.0), glow * 2.0);

    return vec4(color * (1.0 - d * 0.8), 1.0);
}
