vec4 Effect2(vec2 uv) {
    // Scale and animate grid position
    vec2 st = uv * 10.0 + iTime;

    // Add noise to create organic movement
    vec2 offset = noise(vec3(st, 0.0)) * 0.5;
    st += offset;

    // Create triangle pattern using barycentric coordinates
    vec2 pos = fract(st);
    float d1 = length(pos - vec2(0.0));
    float d2 = length(pos - vec2(1.0));
    float d3 = length(pos - vec2(0.5, 0.5));

    // Calculate color based on distance and time
    float hue = (d1 + d2 + d3) * 0.2 + iTime * 0.5;
    vec3 color = hsl2rgb(vec3(hue, 0.7, 0.6));

    // Add turbulence for more dynamic effect
    float turb = turbulent(vec3(st, iTime)) * 0.3;
    color *= 1.0 + turb;

    return vec4(color, 1.0);
}
