vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal pattern
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x);
    float radius = length(st);

    // Add rotation over time
    angle += iTime * 0.5;

    // Create hexagonal tiling effect
    float scale = 4.0;
    vec2 polar = vec2(radius * scale, angle);
    vec2 tile = floor(polar + 0.5);

    // Calculate distance from center of nearest hexagon
    vec2 tileCenter = fract(polar) - 0.5;
    float dist = length(tileCenter);

    // Create smooth color transitions using HSL
    vec3 hslColor = vec3(fract(angle * 0.166 + iTime * 0.1), 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add fading effect based on distance from center
    float fade = smoothstep(0.4, 0.6, dist);
    rgbColor *= mix(vec3(1.0), vec3(0.0), fade);

    return vec4(rgbColor, 1.0);
}
