vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5;

    // Create rotating sine wave pattern
    float freq = 8.0;
    float wave = sin(r * freq + a * 4.0) * 0.5 + 0.5;
    wave = mix(wave, noise(vec2(uv * 16.0)), 0.3);

    // Dynamic RGB shifts
    vec3 color;
    color.r = sin(a + iTime * 0.7 + 0.0) * 0.5 + 0.5;
    color.g = sin(a + iTime * 0.5 + 2.0) * 0.5 + 0.5;
    color.b = sin(a + iTime * 0.3 + 4.0) * 0.5 + 0.5;

    // Add noise and turbulence
    float turb = turbulent(vec2(uv * 16.0)) * 0.3;
    color += vec3(turb);

    // Create smooth transitions
    float blend = smoothstep(0.7, 1.0, r);
    color = mix(color, hsl2rgb(vec3(iTime * 0.1, 1.0, 0.8)), blend);

    return vec4(color, 1.0);
}
