vec4 Effect2(vec2 uv) {
    // Moving bands parameters
    float time = iTime * 0.5;
    vec2 st = uv * 10.0; // Scale up for more bands

    // Horizontal bands moving right/left
    float band1 = sin(st.x + time) * 0.5 + 0.5;

    // Vertical bands moving up/down
    float band2 = cos(st.y - time) * 0.5 + 0.5;

    // Add noise perturbation to both bands
    vec3 noise1 = noise(vec3(uv * 4.0, time));
    vec3 noise2 = noise(vec3(uv * 6.0, time));

    band1 += noise1.x * 0.2;
    band2 += noise2.y * 0.2;

    // Combine bands with turbulence
    float combined = turbulent(vec2(band1, band2)) * 0.5 + 0.5;

    // Pulsating color based on time and position
    vec3 color1 = hsl2rgb(vec3(time * 0.1 + uv.x, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(time * 0.15 - uv.y, 1.0, 0.7));

    // Mix colors based on band values
    vec3 finalColor = mix(color1, color2, combined);

    // Add pulsating brightness
    float brightness = sin(time * 4.0) * 0.5 + 0.5;
    finalColor *= brightness;

    return vec4(finalColor, 1.0);
}
