vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create base plasma pattern with noise and turbulence
    vec3 color = hsl(
        0.5 + turbulent(vec3(r*4.0 + iTime*0.5, a*2.0, 0.0)) * 0.5,
        1.0,
        0.5 + sin(iTime*0.5) * 0.5
    );
    // Add flowing light trails using noise and time
    vec3 trail = hsl(
        0.5 + turbulent(vec3(r*2.0 + iTime, a*4.0, 0.0)) * 0.5,
        1.0,
        0.5 + sin(iTime) * 0.5
    );
    // Create distance-based blending for core and trails
    float core = smoothstep(0.8, 1.0, r);
    float trailBlend = smoothstep(0.9, 1.0, r);
    // Combine colors with trail effect
    color = mix(color, trail * 2.0, trailBlend);
    // Add outer glow effect
    float glow = smoothstep(0.7, 1.0, r);
    color *= (1.0 - glow) * 0.5 + 0.5;
    // Convert HSL to RGB and return as vec4
    return vec4(hsl2rgb(color), 1.0);
}
