vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time-based movement
    vec2 st = fract(uv * 5.0 + sin(iTime) * 0.5);

    // Create a noise pattern with turbulence
    float n1 = turbulent(st * 3.0 + sin(iTime * 0.7));
    float n2 = turbulent(st * 6.0 + cos(iTime * 0.5));

    // Combine noise patterns
    float pattern = (n1 * 0.5 + 0.5) * (n2 * 0.3 + 0.7);

    // Convert to HSL color with dynamic hue
    vec3 hslColor = vec3(
        fract(pattern * 0.4 + iTime * 0.1),
        0.8, // Saturation
        0.5  // Lightness
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
