vec4 Effect2(vec2 uv) {
    // Create base wave patterns
    float base = cos(uv.x * pi + iTime) * 2.0 + 1.0;
    float vertical = sin(iTime * 0.5 + uv.y * pi) * 2.0 + 1.0;

    // Add noise and turbulence for more dynamic patterns
    vec3 noisePattern = noise(vec3(uv * 10.0, iTime)) * turbulent(uv * 5.0);

    // Combine patterns with color transformations
    vec3 color = hsl2rgb(vec3(
        (base + vertical) * 0.5,
        sin(iTime * 0.2 + uv.x * pi) * 0.5 + 0.5,
        noisePattern.z * 0.5 + 0.5
    ));

    // Add flowing movement using cosine waves
    color += cos(uv.xx * pi * 4.0 + iTime * 2.0) * 0.3;
    color += noise(vec2(uv * 10.0)) * vertical * 0.2;

    return vec4(color, 1.0);
}
