vec4 Effect2(vec2 uv) {
    // Translate UV to center coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Add rotation based on time
    float angle = sin(iTime * 0.5 + 3.1415926) * 0.5 + 3.1415926;
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Add turbulence
    float turb = turbulent(rotated * 0.5 + iTime);

    // Create sine wave patterns with different frequencies and phases
    float wave1 = sin(rotated.x * 4.0 + iTime) * 0.5;
    float wave2 = sin(rotated.y * 6.0 - iTime) * 0.3;
    float wave3 = sin((rotated.x + rotated.y) * 8.0 + iTime * 2.0) * 0.2;

    // Combine waves and add turbulence
    float pattern = (wave1 + wave2 + wave3) * 0.5 + turb * 0.3;

    // Convert to color using HSL
    vec3 color = hsl2rgb(vec3(
        fract(pattern * 0.5 + iTime) * 360.0,
        0.7,
        0.5
    ));

    // Mix with dark background
    vec3 finalColor = mix(vec3(0.1), color, pattern * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
