vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Particle grid size and count
    const float gridSize = 16.0;
    const int particleCount = 8;

    // Calculate base position
    vec2 pos = uv * gridSize;
    vec2 ipos = floor(pos);
    vec2 fpos = fract(pos);

    // Add noise-based movement to particles
    vec2 offset = noise(vec3(ipos + iTime, 0.0)) * 0.5;
    vec2 particlePos = ipos + 0.5 + offset;

    // Calculate distance from fragment to particle
    float dist = length(fpos - fract(particlePos));

    // Create glow effect with turbulence
    float glow = max(1.0 - dist * 4.0, 0.0);
    glow *= turbulent(vec3(uv * 8.0 + iTime, 0.0)) * 0.5 + 0.5;

    // Add color and shadow effects
    vec3 baseColor = hsl2rgb(vec3(0.6, 1.0, 0.5));
    vec3 glowColor = baseColor * glow * 4.0;
    float shadow = max(1.0 - dist * 8.0, 0.0);

    // Combine effects
    finalColor += glowColor;
    finalColor *= (1.0 - shadow * 0.5);

    return vec4(finalColor, 1.0);
}
