vec4 Effect2(vec2 uv) {
    // Base pulsation
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Layered sine waves with noise modulation
    vec2 st = uv * 10.0;
    float wave1 = sin(st.x * 3.0 + iTime) * 0.5;
    float wave2 = sin(st.y * 4.0 + iTime * 0.7) * 0.4;
    float noiseLayer = turbulent(vec2(uv * 8.0)) * 0.3;

    // Combine waves and add turbulence
    float pattern = (wave1 + wave2 + noiseLayer) * 0.5 + 0.5;

    // Color transformation with pulsation
    vec3 color = hsl2rgb(vec3(
        mod(pulse * 360.0, 360.0),
        0.7,
        pattern * 0.8 + 0.2
    ));

    // Add brightness variation
    float brightness = sin(iTime) * 0.5 + 0.5;
    color *= (pattern + noiseLayer * 0.3) * (1.0 + brightness);

    return vec4(color, 1.0);
}
