vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Calculate distance from center
    float dist = length(st);

    // Create expanding rings using time and noise
    float ring = sin(dist * 5.0 + iTime) * 0.5 + 0.5;

    // Add chromatic aberration effect
    vec3 color = vec3(0.0);

    // Red channel with slight offset
    color.r = turbulent(vec2(st.x + 0.1*sin(iTime), st.y + 0.1*cos(iTime)) * 4.0 + iTime) * ring;

    // Green channel with different offset
    color.g = turbulent(vec2(st.x - 0.1*cos(iTime), st.y + 0.1*sin(iTime)) * 4.0 + iTime) * ring;

    // Blue channel with another offset
    color.b = turbulent(vec2(st.x + 0.1*cos(iTime), st.y - 0.1*sin(iTime)) * 4.0 + iTime) * ring;

    return vec4(color, 1.0);
}
