vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = sin(iTime * 0.5 + 1.0);
    vec2 rotatedUV = cos(angle)*uv.x - sin(angle)*uv.y;
                   sin(angle)*uv.x + cos(angle)*uv.y;
    // Create hexagonal grid pattern
    vec2 st = rotatedUV * 4.0;
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);
    // Calculate distance from center of hexagon
    vec2 center = (i_st + 0.5) / 4.0;
    float dist = length(uv - center);

    // Create pulsating color based on distance and time
    float hue = mod(length(center) * 10.0 + iTime, 1.0);
    vec3 hslColor = vec3(hue, 0.7, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);
    // Add turbulence for dynamic effect
    float noiseVal = turbulent(vec3(st.x, st.y, iTime * 0.1));
    rgbColor *= 1.0 + noiseVal * 0.5;
    // Create hexagonal shape using distance field
    if (dist > 0.2) {
        return vec4(0.0, 0.0, 0.0, 0.0);
    }
    return vec4(rgbColor, 1.0);
}
