vec4 Effect2(vec2 uv) {
    // Center the coordinates and scale them up
    vec2 st = (uv - 0.5) * 3.0;

    // Calculate distance from center
    float d = length(st);

    // Add time-based pulsation
    d += sin(iTime * 0.5 + d * 10.0) * 0.2;

    // Create base color using HSL conversion
    vec3 color = hsl(
        sin(iTime * 0.2 + d * 5.0) * 0.5 + 0.5,   // Hue
        0.7,                                       // Saturation
        sin(d * 4.0 - iTime * 0.5) * 0.5 + 0.5    // Lightness
    );

    // Add noise texture for more detail
    float n = noise(vec3(st * 10.0, iTime));
    color = mix(color, hsl2rgb(vec3(
        sin(iTime * 0.3 + d * 3.0) * 0.5 + 0.5,
        0.8,
        0.6
    )), n * 0.4);

    // Add secondary layer for depth
    float d2 = length(st * 0.5);
    color += hsl(
        sin(iTime * 0.1 + d2 * 8.0) * 0.5 + 0.5,
        0.6,
        sin(d2 * 4.0 - iTime * 0.3) * 0.5 + 0.5
    ) * (1.0 - n) * 0.3;

    // Vary brightness based on distance and time
    float brightness = sin(iTime * 0.2 + d * 4.0) * 0.5 + 0.5;
    color *= brightness * (1.0 - d * 0.1);

    return vec4(color, 1.0);
}
