vec4 Effect2(vec2 uv) {
    // Create multiple sine waves with varying frequencies and phases
    float d = 0.0;

    // Wave 1
    d += sin(uv.x * 5.0 + iTime * 2.0 + 3.14159) * 0.5;
    // Wave 2
    d += sin(uv.y * 7.0 - iTime * 1.5 + 1.5708) * 0.4;
    // Wave 3
    d += sin((uv.x + uv.y) * 4.0 + iTime * 3.0 + 2.3562) * 0.3;
    // Wave 4
    d += sin((uv.x - uv.y) * 6.0 - iTime * 2.5 + 0.7854) * 0.2;

    // Scale and offset the interference pattern
    d = (d * 0.5 + 0.5);

    // Create color shifts using HSL
    vec3 color = hsl(iTime * 0.1, 0.7, d * 0.6 + 0.4);

    // Add some noise to create texture
    float noiseVal = turbulent(uv * 8.0 + iTime) * 0.2;
    color += noiseVal;

    return vec4(hsl2rgb(color), 1.0);
}
