vec4 Effect2(vec2 uv) {
    // Translate UV coordinates to center
    vec2 st = uv - 0.5;

    // Rotate coordinates based on time
    float angle = iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;

    // Convert to polar coordinates
    float r = length(rotated);
    float theta = atan2(rotated.y, rotated.x) + iTime * 0.5;

    // Add noise and turbulence
    float n = noise(vec2(uv.x * 4.0 + iTime, uv.y * 4.0));
    float t = turbulent(vec2(theta, r * 0.1));

    // Create color from HSL values
    vec3 hslColor = vec3(
        theta * 0.15,
        sin(r * 0.5 + iTime) * 0.5 + 0.5,
        t * 0.5 + 0.5
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
