vec4 Effect2(vec2 uv) {
    // Center and scale coordinates
    vec2 st = (uv - 0.5) * 4.0;

    // Convert to polar coordinates
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create swirling pattern by adding time-dependent rotation
    theta += iTime * 3.0;

    // Add concentric circles with varying radius
    float circle = sin(theta + (1.0 / r) * 5.0) * 0.5 + 0.5;

    // Add turbulence for organic movement
    vec2 noiseUV = uv * 4.0 + iTime;
    float turb = turbulent(noiseUV);

    // Create color transitions using HSL
    vec3 color = hsl(
        (theta / (2.0 * PI) + 0.5) * 0.6,  // Hue varies with angle
        0.7 + turb * 0.3,                  // Saturation modulation
        0.5 + sin(r * 4.0 - iTime) * 0.2   // Lightness variation
    );

    return vec4(color, 1.0);
}
