vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = (uv - 0.5) * 3.0;

    // Polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5;

    // Add turbulence to both radius and angle
    float turb = turbulent(vec2(r, a)) * 0.5 + 0.5;
    turb *= sin(iTime * 0.3 + r * 10.0);

    // Create color variations using HSL
    vec3 color1 = hsl(a * 0.1 + iTime * 0.2, 0.8, 0.6);
    vec3 color2 = hsl(a * 0.15 - iTime * 0.3, 0.7, 0.5);

    // Combine colors with noise
    vec3 finalColor = mix(color1, color2, turb);
    finalColor *= sin(r * 4.0 + iTime) * 0.5 + 0.5;
    finalColor += noise(st * 0.5 + iTime) * 0.3;

    return vec4(finalColor, 1.0);
}
