vec4 Effect2(vec2 uv) {
    // Create flowing river bed shape using sine wave with time modulation
    float riverBed = sin(uv.y * 8.0 + iTime) * 0.5;

    // Calculate distance from river bed
    float dist = uv.x - riverBed;

    // Create color gradient for water
    vec3 baseColor = hsl2rgb(vec3(190.0, 0.7, 0.6));
    vec3 edgeColor = hsl2rgb(vec3(240.0, 0.5, 0.8));

    // Mix colors based on distance from river bed
    float mixAmount = smoothstep(-0.1, 0.1, dist);
    vec3 color = mix(baseColor, edgeColor, mixAmount);

    // Add wave distortion using noise and turbulence
    vec2 distortedUV = uv + vec2(turbulent(uv * 4.0 + iTime), sin(uv.x * 8.0 + iTime) * 0.1);
    float wave = sin(distortedUV.x * 8.0 + iTime * 2.0);

    // Add dynamic color modulation
    vec3 finalColor = mix(color, hsl2rgb(vec3(240.0 + iTime * 5.0, 0.6, 0.7)), wave * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
