vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal symmetry
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create hexagonal grid pattern using sine and cosine functions
    a *= 6.0; // Multiply angle by 6 for hexagonal symmetry
    vec2 grid = vec2(sin(a), cos(a)) * 0.5 + 0.5;

    // Add turbulence to create flowing edges
    float noiseVal = turbulent(vec3(st.x, st.y, iTime * 0.1));
    r += noiseVal * 0.2;
    // Create pulsating color effect using HSL
    vec3 colorHSL = vec3(
        mod(iTime * 0.5 + a * 0.1, 1.0), // Hue variation over time and angle
        0.7,                              // Saturation
        0.5 + noiseVal * 0.2             // Lightness modulation with noise
    );

    // Convert HSL to RGB and output
    vec3 color = hsl2rgb(colorHSL);

    return vec4(color, 1.0);
}
