vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns
    float time = iTime * 0.5;

    // Combine multiple sine waves with different frequencies and amplitudes
    float wave1 = sin(uv.x * 8.0 + time) * 0.5;
    float wave2 = cos(uv.y * 4.0 - time) * 0.3;
    float wave3 = sin((uv.x + uv.y) * 6.0 + time * 2.0) * 0.2;

    // Combine waves and add noise
    float combined = wave1 + wave2 + wave3;
    combined += noise(uv * 4.0 + vec2(time)) * 0.1;

    // Create pulsating color effect using HSL
    vec3 colorHSL = vec3(
        mod(time * 0.5, 1.0),   // Hue that changes over time
        0.7 + noise(uv * 8.0) * 0.2, // Saturation with some noise variation
        0.5 + combined * 0.4    // Lightness affected by the wave pattern
    );

    // Convert HSL to RGB and add turbulence effect
    vec3 rgbColor = hsl2rgb(colorHSL);
    float turb = turbulent(uv * 16.0 + vec2(time)) * 0.5;
    rgbColor += turb * 0.4;

    return vec4(rgbColor, 1.0);
}
