vec4 Effect2(vec2 uv) {
    // Create a dark gradient background
    vec3 bg = hsl2rgb(vec3(0.5, 1.0, 0.1));

    // Add geometric patterns with sine wave movement
    float time = iTime * 0.2;
    vec2 st = uv * 4.0 + time;

    // Create hexagonal pattern
    vec2 hexCoords = vec2(
        sin(st.x * 0.5) * cos(st.y * 0.5),
        cos(st.x * 0.5) * sin(st.y * 0.5)
    );

    // Add triangular movement
    float triangle = sin(st.x + time) * cos(st.y + time);

    // Combine geometric patterns with noise
    vec2 noiseCoords = uv * 16.0 + vec2(time, time);
    float noiseValue = noise(noiseCoords);

    // Create color transitions using HSL
    vec3 color1 = hsl2rgb(vec3(0.1 + time * 0.1, 0.8, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.4 + time * 0.1, 0.7, 0.6));

    // Mix patterns and colors
    float mixValue = abs(sin(time)) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixValue);

    // Add turbulence effect
    vec2 turbulentCoords = uv * 8.0 + vec2(time, time);
    float turbulentValue = turbulent(turbulentCoords) * 0.5 + 0.5;

    // Combine all elements
    vec3 result = bg * (1.0 - turbulentValue) + finalColor * turbulentValue;

    return vec4(result, 1.0);
}
