vec4 Effect2(vec2 uv) {
    // Time-based parameters for animation
    float timeScale = sin(iTime * 0.5) + 1.0;
    float timeOffset = iTime * 0.3;

    // Base noise for particle field
    vec2 st = uv * 8.0; // Multiply by frequency
    float baseNoise = noise(st + timeOffset);

    // Create light trail effect using turbulence
    float trail = turbulent(uv * 4.0 + timeOffset) * 0.5;

    // Color transformation with HSL
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.2 + uv.x * 10.0) * 0.5 + 0.5, // Hue variation
        0.7, // Saturation
        0.8 // Lightness
    ));

    // Combine effects with reactive field
    vec3 finalColor = color * (baseNoise + trail);
    finalColor *= sin(uv.x * 2.0 + iTime) * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
