vec4 Effect2(vec2 uv) {
    // Moving stripes with time-based movement
    float time = iTime * 0.5;

    // Create base stripe pattern
    vec3 color = sin(uv.xyy * 10.0 + time) * 0.5 + 0.5;

    // Add noise and turbulence to create dynamic movement
    vec2 st = uv * 10.0;
    float noiseVal = noise(st + time);
    float turbulentVal = turbulent(vec3(uv, time));

    // Create color variations using HSL
    vec3 hslColor = vec3(
        sin(time) * 0.5 + 0.5,
        1.0,
        0.6
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine effects with noise and turbulence
    color *= mix(noiseVal, turbulentVal, 0.5) * 0.8 + 0.2;
    color += rgbColor * 0.4;

    // Add movement to stripes using atan2 for direction
    float angle = atan2(sin(time), cos(time));
    vec2 offset = vec2(cos(angle), sin(angle)) * 0.1;
    color *= noise(uv + offset);

    return vec4(color, 1.0);
}
