vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create rotating hexagon pattern using polar coordinates
    float theta = atan2(st.y, st.x) + iTime * 0.5;
    float r = length(st);

    // Hexagonal tiling pattern
    float sawtooth = mod(theta * 6.0, PI * 2.0);
    sawtooth = sin(sawtooth) / 4.0 + 1.0;
    sawtooth = min(sawtooth, 1.0);

    // Distance from hexagon center
    float dist = r - sawtooth;
    float hex = step(0.3, dist);

    // Pulsating color based on time
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.5, 1.0),   // Hue
        0.7,                     // Saturation
        0.5 + sin(iTime) * 0.4   // Lightness pulsation
    ));

    // Add noise overlay
    vec2 noiseScale = st * 8.0;
    float noiseVal = noise(noiseScale) * 0.3;
    color += noiseVal;

    return vec4(hex * color, 1.0);
}
