vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float angle = atan2(uv.y, uv.x);
    float radius = length(uv);

    // Create orbiting motion using time
    angle += iTime * 0.5;

    // Add noise for light trail effect
    vec2 pos = uv + noise(vec3(angle, radius, iTime)) * 0.1;
    float trail = turbulent(vec2(pos.x * 10.0, pos.y * 10.0));

    // Create vibrant color transitions
    vec3 baseColor = hsl(iTime * 0.5 + angle * 0.2, 0.7, 0.6);
    vec3 trailColor = mix(baseColor, hsl(angle * 0.8 + iTime * 0.3, 0.9, 0.7), 0.3);

    // Combine colors and trails
    vec3 finalColor = baseColor * (1.0 - trail) + trailColor * trail;

    // Add distance-based fading
    float distFade = smoothstep(0.8, 1.2, radius);
    finalColor *= distFade;

    return vec4(finalColor, 1.0);
}
