vec4 Effect2(vec2 uv) {
    // Create a base color using noise and time
    vec3 baseColor = hsl(
        noise(uv * 0.5 + iTime) * 0.5 + 0.5,
        noise(uv * 0.7 - iTime * 0.1) * 0.5 + 0.5,
        0.6
    );

    // Add turbulence to create movement in the background
    baseColor *= turbulent(uv * 2.0 + iTime);

    // Create rotating sine wave patterns
    float angle = atan2(uv.y - 0.5, uv.x - 0.5) + iTime;
    float distance = length(uv - 0.5);

    // Multiple frequency waves for interference effect
    float wave1 = sin(angle * 4.0 + iTime * 2.0) * cos(distance * 8.0);
    float wave2 = sin(angle * 6.0 + iTime * 3.0) * cos(distance * 12.0);

    // Combine waves and create overlay effect
    float interference = (wave1 + wave2) * 0.5;
    vec3 waveColor = hsl(
        0.5,
        1.0,
        0.5 + abs(interference) * 0.5
    );

    // Overlay waves on base color with varying strength
    float waveStrength = (turbulent(uv * 4.0 + iTime * 2.0) + 1.0) * 0.5;
    vec3 finalColor = mix(baseColor, waveColor, waveStrength);

    return vec4(hsl2rgb(finalColor), 1.0);
}
