vec4 Effect2(vec2 uv) {
    // Create a base noise pattern with time offset
    float speed = 0.5;
    vec2 st = uv * 10.0 + iTime * speed;

    // Generate turbulent noise
    float noise1 = turbulent(st);
    float noise2 = turbulent(st * 50.0 + iTime);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1, 1.0) + noise1 * 0.5,
        0.7,
        0.6
    );

    // Convert to RGB and mix with additional noise layers
    vec3 color = hsl2rgb(hslColor);
    color *= abs(noise(vec2(uv.x*50.0, uv.y + iTime * 0.1)));

    return vec4(color, 1.0);
}
