vec4 Effect2(vec2 uv) {
    // Create rotating checkerboard pattern
    vec2 st = uv;
    float speed = 0.5;

    // Rotate coordinates
    float angle = sin(iTime * speed) * pi;
    vec2 rotatedUV = vec2(
        (st.x - 0.5) * cos(angle) - (st.y - 0.5) * sin(angle),
        (st.x - 0.5) * sin(angle) + (st.y - 0.5) * cos(angle)
    ) + 0.5;

    // Checkerboard pattern
    vec2 grid = floor(rotatedUV * 10.0);
    float check = fract(grid.x + grid.y);

    // Calculate distance from center
    vec2 center = vec2(0.5, 0.5);
    float dist = length(uv - center) * 2.0;

    // Pulsing color based on distance and time
    float pulse = sin(iTime + dist * 10.0);
    vec3 color = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 0.0, 1.0), (pulse + 1.0)/2.0);

    // Combine checkerboard and pulsing color
    return vec4(color * check, 1.0);
}
