vec4 Effect2(vec2 uv) {
    // Rotate and scale coordinates over time
    float t = iTime * 0.5;
    vec2 st = uv - 0.5;
    st *= sin(t + 1.0) * 2.0 + 2.0; // Scale factor

    // Create kaleidoscopic effect by rotating and mirroring
    float angle = t * 4.0;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rot * st;

    // Add noise-based turbulence
    vec3 noisePos = vec3(st, t);
    float turb = turbulent(noisePos * 0.5) * 0.5 + 0.5;

    // Create geometric patterns with rainbow colors
    vec2 mirrored = mod(abs(st), 1.0);
    mirrored = mirrored < 0.5 ? mirrored : 1.0 - mirrored;

    float dist = length(mirrored - 0.5) * 3.0;
    float shape = step(0.7, dist);

    // Rainbow color transitions
    vec3 color = hsl2rgb(vec3(turb * 0.6 + 0.1, 0.8, 0.5));
    color *= shape;

    // Add noise variation
    color += hsv2rgb(vec3(turb * 0.4 + 0.3, 0.7, 0.3)) * 0.3;

    return vec4(color, 1.0);
}
