vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create flowing pattern with noise and sine waves
    vec2 st = uv * 10.0 + vec2(time, 0.0);
    float noiseVal = turbulent(st) * 0.5 + 0.5;

    // Add sine wave modulation
    float waveX = sin(uv.x * 8.0 + time * 2.0) * 0.5 + 0.5;
    float waveY = sin(uv.y * 8.0 + time * 3.0) * 0.5 + 0.5;

    // Combine noise and waves
    vec3 colorMix = vec3(
        noiseVal * waveX,
        noiseVal * waveY,
        turbulent(vec2(time, uv.x * 5.0)) * 0.5 + 0.5
    );

    // Create dynamic color shifts using HSL
    float hue = time * 0.1 + length(uv) * 0.3;
    vec3 hslColor = hsl2rgb(vec3(hue, 0.8, 0.8));

    // Mix colors and add lighting effect
    vec3 finalColor = mix(colorMix, hslColor, 0.7);
    float lightFactor = turbulent(vec2(time * 2.0, uv.y * 5.0)) * 0.5 + 0.5;
    finalColor *= lightFactor;

    return vec4(finalColor, 1.0);
}
