vec4 Effect2(vec2 uv) {
    // Create flowing movement effect
    vec2 st = uv * 5.0 + vec2(iTime * 0.1, 0.0);

    // Add undulating pattern
    float flow = sin(st.x * 0.5 + iTime) * 0.5;
    st.y += flow;

    // Generate noise-based ripples
    float ripple1 = noise(vec2(st.x * 0.5, st.y)) * 0.3;
    float ripple2 = noise(vec2(st.x * 0.7 + iTime, st.y * 0.6)) * 0.4;

    // Combine ripples and add turbulence
    float totalRipple = (ripple1 + ripple2) * turbulent(vec2(st.x * 0.3, st.y * 0.5));

    // Create color shifts using HSL
    vec3 baseColor = hsl(0.6 + iTime * 0.1, 0.7, 0.5);
    vec3 rippleColor = hsl(0.8 + iTime * 0.2, 0.6, 0.4);

    // Mix colors based on ripples
    vec3 finalColor = mix(baseColor, rippleColor, totalRipple * 0.5 + 0.5);

    // Convert to RGB and output
    return vec4(hsl2rgb(finalColor), 1.0);
}
