vec4 Effect2(vec2 uv) {
    // Create a time-based animation component
    float t = iTime * 0.5;

    // Generate undulating sine wave patterns with different frequencies and amplitudes
    float wave1 = sin(uv.x * 8.0 + t) * 0.5;
    float wave2 = sin(uv.y * 4.0 - t) * 0.3;
    float wave3 = sin((uv.x + uv.y) * 6.0 + t * 2.0) * 0.2;

    // Combine waves and add turbulence for more dynamic movement
    float displacement = wave1 + wave2 + wave3 + turbulent(uv * 4.0 + vec2(t, t)) * 0.1;

    // Create color transition from warm reds to cool blues using HSL colors
    vec3 color = hsl(
        sin(displacement * 2.0 + t) * 0.5 + 0.5,  // Hue: oscillates between red and blue
        0.7,                                      // Saturation
        0.5 + displacement * 0.2                  // Lightness modulation
    );

    // Convert HSL to RGB for final output
    vec3 rgbColor = hsl2rgb(color);

    return vec4(rgbColor, 1.0);
}
