vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create a swirling pattern using polar coordinates
    vec2 polarUV = vec2(r, theta);

    // Use noise and turbulence to create organic patterns
    float noise1 = turbulent(polarUV * 5.0 + iTime);
    float noise2 = noise(vec3(st.x * 5.0, st.y * 5.0, iTime));

    // Create a pulsating effect using sine wave
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;

    // Generate color based on angle and radius
    vec3 color = hsl2rgb(vec3(
        theta * 0.15 + iTime * 0.1,   // Hue varies with angle and time
        0.7 + noise1 * 0.3,           // Saturation with noise variation
        0.5 + pulse * 0.5            // Value/pulsation effect
    ));

    // Add radial light trails using sine pattern
    float trail = sin(r * 20.0 - iTime) * 0.5 + 0.5;
    color *= mix(1.0, 3.0, trail);

    // Combine noise layers for more complexity
    color += hsv2rgb(vec3(
        theta * 0.1 + iTime * 0.2,
        0.6,
        noise2 * 0.5
    )) * 0.5;

    return vec4(color, 1.0);
}
