vec4 Effect2(vec2 uv) {
    // Center the coordinates and scale them appropriately
    vec2 st = uv * 2.0 - 1.0;

    // Convert to polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Create a time-varying position
    vec2 pos = vec2(r + iTime*0.5, a);

    // Use turbulence to create a complex pattern
    float t = turbulent(pos * 10.0) * 0.5 + 0.5;

    // Create color variations using HSL
    vec3 color = hsl(t * 360.0, 0.8, 0.5);

    // Add more complexity with noise
    float n = noise(pos * 20.0 + iTime) * 0.5 + 0.5;
    color *= n;

    return vec4(color, 1.0);
}
