vec4 Effect2(vec2 uv) {
    // Scale and offset UV coordinates for grid pattern
    vec2 st = uv * 10.0;

    // Calculate grid cell position
    vec2 pos = mod(st, 1.0);

    // Time-based rotation angle
    float time = iTime * 0.5;
    float angle = sin(time) * 0.5 + 1.5708; // Offset by PI/2

    // Rotation matrix
    mat2 rot = mat2(cos(angle), -sin(angle),
                    sin(angle), cos(angle));

    // Apply rotation to position
    vec2 rotatedPos = rot * (pos - 0.5) + 0.5;

    // Add noise for texture
    float noiseVal = noise(vec3(rotatedPos, time)) * 0.1;

    // Calculate color with rainbow effect
    vec3 hslColor = vec3(
        mod(time + rotatedPos.x * 2.0 + rotatedPos.y * 3.0, 1.0), // Hue variation
        0.7 + noiseVal * 0.3, // Saturation
        0.5 + noiseVal * 0.5  // Lightness
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
