vec4 Effect2(vec2 uv) {
    // Create a coordinate system centered at (0,0)
    vec2 st = uv * 2.0 - 1.0;

    // Add time-based rotation
    float t = iTime * 0.5;
    st = mat2(cos(t), sin(t), -sin(t), cos(t)) * st;

    // Create hexagonal grid pattern
    vec2 polar = vec2(length(st), atan2(st.y, st.x));
    float theta = polar.y * 6.0; // Hexagonal symmetry

    // Create tessellation pattern with shifting colors
    float gridX = sin(polar.x * 10.0 + theta + t) * 0.5 + 0.5;
    float gridY = cos(polar.x * 10.0 - theta + t) * 0.5 + 0.5;

    // Combine patterns and add noise
    float pattern = sin(gridX * pi) * cos(gridY * pi);
    pattern += noise(vec2(st.x * 4.0, st.y * 4.0)) * 0.3;

    // Create color transitions
    vec3 color1 = hsl(0.6 + t*0.1, 0.8, 0.5);
    vec3 color2 = hsl(0.2 + t*0.1, 0.8, 0.7);

    // Mix colors based on pattern
    vec3 finalColor = mix(color1, color2, abs(pattern * 0.5 + 0.5));

    // Add radial motion effect
    float dist = length(st) * 0.5;
    finalColor *= sin(dist * pi - t) * 0.5 + 0.5;

    // Output the result with some glow
    return vec4(finalColor, 1.0);
}
