vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Node grid parameters
    const float nodeSize = 0.1;
    const float spacing = 0.25;

    // Calculate node positions
    for(int i = -5; i <= 5; i++) {
        for(int j = -5; j <= 5; j++) {
            vec2 pos = vec2(float(i)*spacing, float(j)*spacing);
            vec2 center = uv + pos;

            // Node color based on time and position
            vec3 nodeColor = hsl((iTime * 0.5) + float(i + j), 1.0, 0.5);

            // Pulsing effect
            float dist = length(center - uv);
            float pulse = sin(iTime * 2.0 + dist * 5.0) * 0.5 + 0.5;

            // Add node glow
            float falloff = exp(-dist * 10.0);
            color += nodeColor * pulse * falloff;

            // Connect nodes with lines
            if(i < 5 && j < 5) {
                vec2 toPos = vec2(float(i+1)*spacing, float(j+1)*spacing);
                vec2 line = stroke(uv, pos, toPos, 0.01);
                color += nodeColor * exp(-line * 10.0);
            }
        }
    }

    // Add background turbulence
    vec3 bg = hsl(iTime * 0.2, 0.5, 0.2) * turbulent(uv * 4.0 + iTime);
    color += bg;

    return vec4(color, 1.0);
}
