vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create orbiting sine wave patterns
    float pattern1 = sin(r * 4.0 + iTime) * 0.5 + 0.5;
    float pattern2 = sin(a * 3.0 - iTime * 2.0) * 0.5 + 0.5;
    // Combine patterns with noise
    vec2 noiseUV = uv * 10.0 + iTime;
    float n = noise(noiseUV);

    // Create shifting hues using HSL
    vec3 color = hsl(r * 0.5 + iTime, 0.7, 0.6);
    color = hsl2rgb(color);
    // Add turbulence for dynamic effect
    vec3 turb = turbulent(vec3(uv * 10.0, iTime));
    color *= mix(pattern1, pattern2, n) * 0.5 + 0.5;
    return vec4(color, 1.0);
}
