vec4 Effect2(vec2 uv) {
    // Create base color with HSL values
    float cx = 0.5;
    float cy = 0.3;
    float cz = 0.6;

    // Add time-based rotation to hue
    cx += sin(iTime * 0.1) * 0.2;

    // Generate noise and turbulence patterns
    float n1 = noise(uv * 5.0 + iTime);
    float n2 = turbulent(vec3(uv * 10.0, iTime));
    float d = (n1 + n2 * 0.5) * 0.5 + 0.5;

    // Create rotating geometric pattern
    vec2 polar = uv - 0.5;
    float angle = atan2(polar.y, polar.x);
    float radius = length(polar);
    angle += iTime * 0.1;

    // Combine patterns with color transformations
    vec3 baseColor = hsl(cx, cy, cz);
    vec3 noiseColor = mix(baseColor, hsl(cx + d * 0.5, cy * 2.0, cz), d);
    vec3 finalColor = hsv2rgb(vec3(angle * 0.15, radius * 0.4 + 0.6, 1.0));

    return vec4(finalColor, 1.0);
}
