vec4 Effect2(vec2 uv) {
    // Create a lattice grid based on UV coordinates
    vec2 st = uv * 100.0; // Scale up for more points
    vec2 p = floor(st + 0.5); // Get integer grid position

    // Add some noise to create interesting patterns
    float n = noise(vec3(p, iTime));

    // Create rotating pattern using polar coordinates
    vec2 offset = vec2(cos(iTime * 2.0 + n * pi), sin(iTime * 2.0 + n * pi)) * 0.5;
    vec2 rotatedUV = uv + offset;

    // Add depth effect using parallax
    vec3 viewPos = perspectiveDivide(vec4(rotatedUV, 1.0, 1.0), mat4(1.0));
    vec2 finalUV = rotatedUV + (viewPos.z * 0.5);

    // Create pulsating color effect
    float hue = iTime * 0.1 + n;
    vec3 color = hsl(hue, 0.7, 0.5);

    // Combine everything with some turbulence
    float turb = turbulent(vec2(p.x, p.y) * 0.1 + iTime);
    return vec4(color * (1.0 + turb * 0.5), 1.0);
}
