vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);
    // Create hexagonal grid pattern
    vec2 grid = vec2(
        sin(theta + 0.5 * pi) * r,
        cos(theta - 0.5 * pi) * r
    );
    // Calculate hexagon position and rotation
    float hexRotation = iTime * 0.1;
    theta += hexRotation;
    // Check if current point is inside a hexagon
    bool isHexagon = false;
    if (abs(grid.x) < 0.5 && abs(grid.y) < 0.5) {
        isHexagon = true;
    }
    // Calculate color based on time and position
    vec3 baseColor = hsl2rgb(vec3(
        mod(0.5 + sin(iTime * 0.5 + r * 10.0), 1.0),
        1.0,
        0.7
    ));
    // Add pulsing effect
    float pulse = sin(iTime * 2.0 + r * 10.0) * 0.3 + 0.7;
    baseColor *= pulse;
    // Apply turbulence to create dynamic movement
    vec2 noiseUV = uv * 5.0 + iTime;
    float turbulence = turbulent(noiseUV);
    theta += turbulence * 0.2;
    // Combine all elements
    if (isHexagon) {
        // Create glowing effect based on distance from center
        float glow = 1.0 - r * 0.5;
        baseColor *= glow;

        return vec4(baseColor, 1.0);
    } else {
        return vec4(vec3(0.0), 1.0); // Black background
    }
}
