vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating circular pattern
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = sin(time * 2.0) * 10.0;

    // Add rotation and geometric effect
    st = mat2(cos(theta), -sin(theta), sin(theta), cos(theta)) * st;
    float pattern = smoothstep(0.7, 1.0, r);

    // Generate noise texture
    vec2 noisePos = uv * 5.0 + time;
    float noiseVal = noise(noisePos) * 0.5 + 0.5;

    // Dynamic color transitions
    vec3 colorBase = hsl2rgb(vec3(sin(time * 0.5) * 0.5 + 0.5, 
                                 sin(time * 0.7) * 0.5 + 0.5,
                                 1.0));

    // Combine elements with light movement
    vec3 finalColor = mix(colorBase, vec3(noiseVal), pattern);
    finalColor *= smoothstep(0.0, 1.0, sin(time + r * 2.0) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
