vec4 Effect2(vec2 uv) {
    float time = float(iTime) * 0.5;

    // Layered sine waves with varying frequencies and amplitudes
    vec2 st = uv * 10.0;
    float total = 0.0;

    for(int i = 0; i < 4; i++) {
        float freq = pow(2., float(i));
        float amp = pow(0.3, float(i));

        // Sine wave pattern
        vec2 sine = sin(vec2(st.x * freq + time, st.y * freq + time + PI/2.)) * 0.5 + 0.5;
        total += length(sine) * amp;
    }

    // Add noise and turbulence
    float noiseVal = turbulent(uv * 10.0 + time);
    total = (total * 0.5 + 0.5) * mix(1.0, noiseVal, 0.3);

    // Color transformation with RGB shifts
    vec3 color = hsl2rgb(vec3(
        sin(time * 0.5) * 0.5 + 0.5,
        0.7,
        0.6
    )) * total;

    // Add time-based RGB shifts
    color.r += sin(time * 0.5);
    color.g += sin(time * 0.7 + PI/2.);
    color.b += sin(time * 1.0 + PI);

    return vec4(color, 1.0);
}
