vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x) + iTime * 0.5; // Rotate over time

    // Create pulsating circles with different frequencies
    float pulse1 = sin(iTime * 0.7) * 0.4 + 0.6;
    float pulse2 = sin(iTime * 0.9 + pi) * 0.3 + 0.7;

    // Calculate distance from center for each pulsating layer
    float d1 = abs(r - pulse1);
    float d2 = abs(r - pulse2);

    // Create color wheel with rotating hues
    vec3 colorWheel = hsl(theta * 0.5, 1.0, 0.5);

    // Combine pulsating circles and color wheel
    vec3 finalColor = colorWheel * (1.0 - smoothstep(0.2, 0.0, d1)) +
                     vec3(1.0) * (1.0 - smoothstep(0.2, 0.0, d2));

    // Add noise for extra texture
    finalColor += noise(vec3(uv * 4.0 + iTime, 0.0)) * 0.2;

    return vec4(finalColor, 1.0);
}
