vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create base sine wave pattern
    vec2 st = uv * 10.0 + time;
    float wave = sin(st.x * 0.5 + time) + sin(st.y * 0.3 + time*0.7);

    // Add geometric shape overlay
    vec2 pos = uv - 0.5;
    float circle = length(pos) * 4.0;
    circle = sin(circle + time) * 0.5 + 0.5;

    // Combine patterns with noise
    float pattern = (wave * 0.5 + 0.5) * circle;
    pattern += turbulent(vec3(uv * 2.0, time)) * 0.3;

    // Color transformation with RGB shifts
    vec3 color = hsl2rgb(vec3(
        fract(time * 0.1 + uv.x * 2.0 + noise(vec3(uv))) * 360.0,
        0.7,
        0.5
    )) * pattern;

    // Add dynamic color shifts using noise
    vec3 noiseCol = hsl2rgb(vec3(
        fract(noise(vec3(uv * 2.0 + time)) * 180.0) * 360.0,
        0.5,
        0.7
    )) * (1.0 - pattern);

    // Combine colors and output
    return vec4(mix(color, noiseCol, 0.3), 1.0);
}
