vec4 Effect2(vec2 uv) {
    // Create time-based animation
    float t = iTime * 0.5;

    // Create two sets of sine waves with different frequencies and phases
    vec2 st1 = uv * 8.0 + vec2(t, 0.0);
    vec2 st2 = uv * 8.0 + vec2(0.0, t * 0.7);

    // Generate wave patterns using sin function
    float wave1 = sin(st1.x) * cos(st1.y + t) * 0.5 + 0.5;
    float wave2 = sin(st2.y) * cos(st2.x - t * 0.3) * 0.5 + 0.5;

    // Combine waves and add noise for turbulence
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(uv * 4.0 + vec2(t)) * 0.3;

    // Create vibrant colors using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3((wave1 * 360.0 + t * 90.0) / 360.0, 0.8, 0.5));
    vec3 color2 = hsl2rgb(vec3((wave2 * 360.0 - t * 90.0) / 360.0, 0.8, 0.5));

    // Mix colors based on wave intensity
    vec3 finalColor = mix(color1, color2, combined);

    return vec4(finalColor, 1.0);
}
