vec4 Effect2(vec2 uv) {
    // Create layered sine wave patterns with noise
    float d = sin(uv.x * 8.0 + iTime) * 0.5;
    d += sin((uv.y + uv.x) * 4.0 + iTime * 0.5) * 0.3;
    d += turbulent(uv * 8.0) * 0.2;

    // Create RGB shifts using HSL color space
    vec3 baseColor = hsl(0.5 + sin(iTime * 0.5) / 4.0, 1.0, 0.5);
    vec3 rgb = hsl2rgb(baseColor);

    // Add dynamic RGB variations
    rgb.r += sin(uv.x * 8.0 + iTime * 2.0) * 0.2;
    rgb.g += sin(uv.y * 8.0 + iTime * 1.5) * 0.2;
    rgb.b += sin((uv.x + uv.y) * 4.0 + iTime) * 0.2;

    return vec4(rgb, 1.0);
}
