vec4 Effect2(vec2 uv) {
    // Animate UV coordinates over time
    vec2 st = uv * 5.0 + iTime;

    // Create turbulence effect
    float turb = turbulent(st);
    vec3 noise1 = vec3(turbulent(st + vec2(0.0, 1.0)), 
                       turbulent(st + vec2(1.0, 0.0)),
                       turbulent(st + vec2(1.0, 1.0)));

    // Generate color gradients using HSL
    vec3 color = hsl2rgb(vec3(
        mod(length(noise1) * 5.0 + iTime, 1.0),
        0.7,
        0.5
    ));

    // Add lighting effect
    float light = max(0.0, 1.0 - length(uv));
    color *= light;

    return vec4(color, 1.0);
}
