vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to create a grid of 20x20 tiles
    vec2 st = uv * 20.0;

    // Get tile position and offset within each tile
    vec2 tile = floor(st);
    vec2 pos = fract(st);

    // Create animated color shifts using time
    float timeFactor = sin(iTime * 0.5) * 0.5 + 0.5;

    // Calculate base color for each tile using noise and position
    float colorValue = sin(tile.x * 0.1 + tile.y * 0.2 + iTime * 0.3);
    colorValue = (colorValue + 1.0) * 0.5; // Scale to 0-1 range

    // Create smooth transitions between colors using turbulence
    float turb = turbulent(vec2(pos.x, pos.y));

    // Combine color and turbulence for final color
    vec3 hslColor = vec3(colorValue + turb * 0.2, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
